(function($) {
//replace widget title 
  $('.title,.widget-title')
        .each(function() {
        var me = jQuery(this);
        me.html(me.html()
            .replace(/^(\w+)/, '<span>$1</span>'));
    });


$('h1.title').each(function(){

    var text = $(this).text().split(' ');
    if(text.length < 2)
        return;

    text[0] = '<span class="secondbc"><span class="secondWord">'+text[0]+'</span></span>';

    $(this).html( text.join(' ') );

});

})(jQuery);
